@keyframes bounceIn {
  from, 20%, 40%, 60%, 80%, to {
    -webkit-animation-timing-function: cubic-bezier(0.215, 0.61, 0.355, 1);
    animation-timing-function: cubic-bezier(0.215, 0.61, 0.355, 1);
  }
  0% {
    opacity: 0;
    -webkit-transform: scale3d(0.3, 0.3, 0.3);
    transform: scale3d(0.3, 0.3, 0.3);
  }
  20% {
    -webkit-transform: scale3d(1.1, 1.1, 1.1);
    transform: scale3d(1.1, 1.1, 1.1);
  }
  40% {
    -webkit-transform: scale3d(0.9, 0.9, 0.9);
    transform: scale3d(0.9, 0.9, 0.9);
  }
  60% {
    opacity: 1;
    -webkit-transform: scale3d(1.03, 1.03, 1.03);
    transform: scale3d(1.03, 1.03, 1.03);
  }
  80% {
    -webkit-transform: scale3d(0.97, 0.97, 0.97);
    transform: scale3d(0.97, 0.97, 0.97);
  }
  to {
    opacity: 1;
    -webkit-transform: scale3d(1, 1, 1);
    transform: scale3d(1, 1, 1);
  }
}
.bounceIn {
  -webkit-animation-duration: 0.75s;
  animation-duration: 0.75s;
  -webkit-animation-name: bounceIn;
  animation-name: bounceIn;
}

.trigger {
  cursor: pointer;
  height: 36px;
  width: 36px;
  flex: 0 0 36px;
  border-radius: 18px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(#db0a40, #001123);
  background-color: #db0a40;
  margin-bottom: 12px;
  border: 2px solid white;
  position: relative;
  box-shadow: 0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23);
  margin-right: 6px;
  transition: 0.15s ease-in-out transform;
}
.trigger:hover {
  transform: scale(1.1);
}
.trigger svg {
  position: relative;
  z-index: 34;
}
.trigger svg path {
  stroke: #ffe8ee;
}
.trigger:before {
  position: absolute;
  border-radius: 16px;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  content: "";
  background: #321b3d;
  opacity: 0;
  transition: all 0.25s ease-in-out;
}
.trigger.clicked:before {
  opacity: 1;
}
.trigger:after {
  position: absolute;
  border-radius: 16px;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  content: "";
  border: 1px solid rgba(0, 17, 35, 0.5);
}
.trigger .can {
  height: 27px;
  width: 10px;
  transform: rotate(36deg);
}
.trigger .can-shadow {
  filter: blur(4px);
  transform: rotate(36deg) scale(1.1);
}

.box {
  border-radius: 4px;
  overflow: hidden;
  display: flex;
  background: linear-gradient(0deg, #001e3c, #db0a40);
  user-select: none;
  position: relative;
  opacity: 0;
  transition: all 0.15s ease-in-out;
  transform: translateY(-20px);
  min-width: 230px;
}
.box.opened {
  opacity: 1;
  transform: translateY(0);
}
.box.poll {
  min-width: 300px;
  width: content-width;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}
.box.main {
  min-height: 345px;
  width: 100%;
  max-width: 230px;
}
.box-inner {
  background: rgba(0, 30, 60, 0.77);
  display: flex;
  flex-flow: column;
  width: 100%;
}
.box-header {
  border-bottom: 1px solid rgba(0, 0, 0, 0.4);
}
.box:after, .box:before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 3px;
  background: #db0a40;
}
.box:before {
  top: initial;
  bottom: 0;
  background: white;
}

.can {
  height: 15px;
  width: 5.56px;
  background-image: url(https://uploads.codesandbox.io/uploads/user/2fca2322-f9cb-49e7-b3e3-ca1e6898abe8/Ftqg-canette.png);
  background-size: cover;
  transform: rotate(15deg);
  position: relative;
  display: block;
  z-index: 2;
}
.can-container {
  position: relative;
}
.can-shadow {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  z-index: 1;
  right: 0;
  background-size: cover;
  filter: blur(3px);
  opacity: 0.5;
}

body {
  min-height: 100vh;
}

.extension {
  font-family: "Titillium Web", sans-serif;
  padding-top: 50px;
}
.extension * {
  -webkit-font-smoothing: antialiased;
