  bottom: 0;
  background: #231C3C;
  z-index: 99;
  opacity: 0.6;
}
.home-header-container:after {
  content: "";
  position: absolute;
  z-index: 999;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  background: linear-gradient(rgba(35, 28, 60, 0.4), #231C3C);
}
.home-header-container .home-header {
  filter: grayscale(100%);
  height: 70px;
  background-size: cover;
  background-position: center center;
}

.home-content {
  position: relative;
  color: white;
