  bottom: 1px;
  padding-left: 4px;
  font-size: 14px;
}
.result-item-percent {
  position: relative;
  z-index: 9;
  font-weight: 900;
  font-size: 12px;
  color: white;
  margin-left: auto;
}

.poll-container {
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  pointer-events: none;
}

.poll.opened {
  pointer-events: auto;
}

.poll-title {
  color: white;
  font-weight: 700;
  text-align: center;
  padding: 0 12px;
  height: 80px;
  display: flex;
  align-items: center;
  font-size: 20px;
  line-height: 1.2;
  justify-content: center;
}

.poll-header {
  padding: 12px;
  padding-bottom: 0;
  font-size: 14px;
  font-weight: 600;
  color: #db0a40;
  display: flex;
  justify-content: center;
}
.poll-header-title {
  font-weight: 700;
  flex: 1;
}

.poll-body {
  min-height: 170px;
}
.poll-body.boximg {
  min-height: 235px;
}

.poll-counter-container {
  text-align: center;
  position: relative;
}

.poll-counter-value {
  position: absolute;
  left: 1px;
  right: 0;
  top: 0;
  bottom: 1px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #d2e8ff;
  font-weight: 800;
}

.progress {
  transform: rotate(-90deg);
  width: 30px;
  height: 30px;
  display: block;
}

.progress__value {
  animation: progress 60s linear infinite;
  stroke-dasharray: 339.292;
  stroke-dashoffset: 339.292;
  stroke: rgba(210, 232, 255, 0.3);
}

.poll-cross {
  flex: 1;
  display: flex;
  justify-content: flex-end;
  opacity: 0.5;
}
.poll-cross svg {
  position: relative;
  top: 2px;
}
.poll-cross svg path {
  stroke: #ffe8ee !important;
}

.poll-answers {
  display: flex;
  padding: 0 12px;
  flex: 1;
  padding-bottom: 18px;
  justify-content: center;
}
.poll-answers.list {
  display: block;
}
.poll-answers .poll-answer {
  text-align: center;
  margin: 0 3px;
  display: flex;
  flex-flow: column;
  cursor: pointer;
  width: 70px;
  max-width: 100px;
}
.poll-answers .poll-answer.selected .poll-answer-label {
  background: white;
  color: #db0a40 !important;
  text-shadow: none;
  font-weight: 900;
}
.poll-answers .poll-answer:hover .poll-answer-image:before {
  opacity: 0;
}
.poll-answers .poll-answer:hover .poll-answer-label {
  color: white;
}
.poll-answers .poll-answer-image {
  height: 70px;
  background-size: cover;
  border-radius: 4px 4px 0 0;
  background-position: center center;
  position: relative;
  cursor: pointer;
}
.poll-answers .poll-answer-image .check {
  position: absolute;
  right: 0;
  bottom: 0;
  margin: 4px;
  width: 16px;
  height: 16px;
}
.poll-answers .poll-answer-image:before {
  content: "";
  opacity: 0;
  background: rgba(0, 17, 35, 0.7);
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  transition: 0.5s ease-in-out;
  border-radius: 4px 4px 0 0;
}
.poll-answers .poll-answer-image:after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  border-left: 1px solid rgba(210, 232, 255, 0.1);
  border-top: 1px solid rgba(210, 232, 255, 0.1);
  border-right: 1px solid rgba(210, 232, 255, 0.1);
  border-radius: 4px 4px 0 0;
}
.poll-answers .poll-answer-label {
  line-height: 1;
  min-height: 32px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  padding-bottom: 1px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #d2e8ff;
  font-size: 14px;
  font-weight: 600;
  bottom: 0;
  left: 0;
  width: 100%;
  background: rgba(210, 232, 255, 0.2);
  text-shadow: 0px 1px 1px rgba(0, 0, 0, 0.5);
  right: 0;
}
.poll-answers .poll-answer-button {
  color: white;
  font-size: 13px;
  text-shadow: 0px -1px 0px rgba(0, 0, 0, 0.4);
  background: #db0a40;
  font-weight: 900;
  padding: 6px 0;
  border-radius: 0 0 4px 4px;
}
.poll-answers .poll-answer-button:hover {
  background: #c30939;
}

.poll-answers:hover .poll-answer-image:before {
  content: "";
  opacity: 1;
}

.poll-answer-percent {
  height: 32px;
  background: rgba(219, 10, 64, 0.3);
  border-radius: 0 0 4px 4px;
  color: white;
  display: flex;
  align-items: center;
  position: relative;
  justify-content: center;
  overflow: hidden;
}
.poll-answer-percent span {
  position: relative;
  font-weight: 900;
  font-size: 14px;
}
.poll-answer-percent-value {
  position: absolute;
  left: 0;
  top: 0;
  bottom: 0;
  background: #db0a40;
}

.poll-answer-list-item {
  box-shadow: 0px 0px 0px 1px #db0a40 inset;
  margin-bottom: 6px;
  border-radius: 4px;
  color: #db0a40;
  text-align: center;
  line-height: 30px;
  font-weight: 600;
  position: relative;
  cursor: pointer;
  transition: 0.15s ease-in-out;
  font-size: 14px;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
}
.poll-answer-list-item .check {
  position: relative;
  z-index: 9;
  top: 1px;
  width: 20px;
  height: 20px;
}
.poll-answer-list-item.result {
  color: white;
  text-align: left;
  padding-left: 6px;
  justify-content: flex-start;
  border-color: transparent;
  box-shadow: none;
}
.poll-answer-list-item.result:hover {
  background: none;
}
.poll-answer-list-item.result:hover .poll-answer-list-item-value:not(.selected) {
  background: rgba(255, 232, 238, 0.3);
}
.poll-answer-list-item:hover {
  background: rgba(219, 10, 64, 0.15);
}
.poll-answer-list-item-name {
  position: relative;
  z-index: 99;
}
.poll-answer-list-item-value {
  height: 100%;
  background: rgba(255, 232, 238, 0.2);
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  transition: background 0.3s ease-in-out;
}
.poll-answer-list-item-value.selected {
  background: #db0a40;
}
.poll-answer-list-item-percent {
  position: absolute;
  right: 0;
  top: 0;
  margin-right: 6px;
  color: #ffe8ee;
}

@keyframes progress {
  from {
    stroke-dashoffset: 0;
  }
  to {
    stroke-dashoffset: -339.292;
  }
}
.popupVotePositionRight .box.poll {
  width: 35vw;
  max-width: 35vw;
  height: 50vh;
  min-width: unset;
  left: auto;
  right: 2.5%;
  transform: translate(0, -50%);
}
.popupVotePositionRight .box.poll .duration {
  left: 0.1vw;
  right: 0;
  top: 0;
  bottom: 0.1vw;
}
.popupVotePositionRight .box.poll .progress {
  width: 3vw;
  height: 3vw;
}
.popupVotePositionRight .box.poll .poll-cross svg {
  top: 0.2vw;
  width: 1.4vw;
  height: 1.4vw;
}
.popupVotePositionRight .box.poll .poll-header {
  font-size: 1.4vw;
  padding: 1.2vw;
  padding-bottom: 0;
}
.popupVotePositionRight .box.poll .poll-title {
  font-size: 1.98vw;
  padding: 0 1.2vw;
  height: 8vw;
}
.popupVotePositionRight .box.poll .poll-answers {
  justify-content: center;
  padding: 0 1.2vw;
}
.popupVotePositionRight .box.poll .poll-answer-list-item {
  margin-bottom: 0.6vw;
  border-radius: 0.4vw;
  line-height: 3vw;
  font-size: 1.4vw;
}
.popupVotePositionRight .box.poll .poll-answer {
  width: 7vw;
  max-width: unset;
  margin: 0 0.3vw;
}
.popupVotePositionRight .box.poll .poll-answer .label {
  font-size: 1.4vw;
}
.popupVotePositionRight .box.poll .poll-answer .poll-answer-label {
  min-height: 3.2vw;
  padding-bottom: 0.1vw;
  font-size: 1.4vw;
}
.popupVotePositionRight .box.poll .poll-answer .poll-answer-button {
  font-size: 1.3vw;
  padding: 0.6vw 0;
  border-radius: 0 0 0.4vw 0.4vw;
}
.popupVotePositionRight .box.poll .poll-answer .poll-answer-image {
  height: 7vw;
  border-radius: 0.4vw 0.4vw 0 0;
}
.popupVotePositionRight .box.poll .poll-answer .poll-answer-image .check {
  margin: 0.4vw;
  width: 1.6vw;
  height: 1.6vw;
}
.popupVotePositionRight .box.poll .poll-answer .poll-answer-percent {
  height: 3.2vw;
  font-size: 1.4vw;
  border-radius: 0 0 0.4vw 0.4vw;
}
.popupVotePositionRight .box.poll .poll-answer .poll-answer-percent span {
  font-size: 1.4vw;
}

.slider {
  flex: 1;
  display: flex;
  transition: all 0.4s ease;
}
.slider.detail {
  transform: translateX(-100%);
}

.profile-header {
  color: white;
  position: relative;
  flex: 0 0 60px;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-top: -1px;
}
.profile-header:after {
  opacity: 0;
  content: "";
  background: #db0a40;
  position: absolute;
  animation: header 0.3s ease-in-out forwards;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  margin: auto;
}
.profile-header .profile-header-content {
  opacity: 0;
  animation: fadeIn 0.3s ease-in-out forwards;
  animation-delay: 0.15s;
  position: relative;
  z-index: 3;
}
.profile-header canvas {
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
}

.profile-name {
  text-align: center;
  font-size: 18px;
  font-weight: 900;
}

.profile-tagline {
  display: flex;
  justify-content: center;
  font-size: 14px;
  font-weight: bold;
  color: #ffe8ee;
}
.profile-tagline > * {
  margin: 0 4px;
}

.profile-can-count {
  display: flex;
  animation: fadeIn 0.3s ease-in-out forwards;
  opacity: 0;
  animation-delay: 0.2s;
}
.profile-can-count .can-container {
  position: relative;
  top: 3px;
  margin-right: 6px;
}

.profile-rank {
  opacity: 0;
  animation: fadeIn 0.3s ease-in-out forwards;
  animation-delay: 0.25s;
}
.profile-rank span {
  opacity: 0.5;
  font-weight: 600;
  margin-right: 2px;
}

.poll-index {
  padding: 0 12px;
  flex: 1;
  display: flex;
  flex-flow: column;
}

.profile {
  flex: 0 0 100%;
  display: flex;
  flex-flow: column;
}

.poll-item {
  display: none;
  align-items: center;
  font-size: 14px;
  border-bottom: 1px solid rgba(210, 232, 255, 0.15);
  animation: fadeIn 0.3s ease-in-out forwards;
  opacity: 0;
  cursor: pointer;
  flex: 0 0 53px;
}
.poll-item-arrow {
  flex: 0 0 10px;
  position: absolute;
  right: 0;
}
.poll-item-arrow img {
  display: block;
}
.poll-item.active {
  display: flex;
}
.poll-item-inner {
  display: flex;
  align-items: center;
  padding: 6px 12px;
  margin-left: -12px;
  margin-right: -12px;
  border-radius: 4px;
  width: 100%;
}
.poll-item-inner:hover {
  background: rgba(0, 0, 0, 0.15);
}
.poll-item.active:nth-child(1) {
  animation-delay: 0.3s;
}
.poll-item.active:nth-child(2) {
  animation-delay: 0.4s;
}
.poll-item.active:nth-child(3) {
  animation-delay: 0.5s;
}
.poll-item.active:last-child {
  border-bottom: none;
}
.poll-item.won .poll-item-answer {
  color: #db0a40;
}
.poll-item.no_answer .poll-item-answer {
  color: rgba(210, 232, 255, 0.5) !important;
}
.poll-item-icon {
  margin-right: 12px;
}
.poll-item-content {
  flex: 1;
}
.poll-item-title {
  max-width: 165px;
  color: white;
  font-weight: 600;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  width: 99%;
  display: inline-block;
  max-width: 180px;
}
.poll-item-answer {
  color: #d2e8ff;
  margin-top: -4px;
  font-weight: 600;
}

@keyframes header {
  from {
    height: 0;
    opacity: 0;
  }
  to {
    opacity: 1;
    height: 60px;
  }
}
.tabs {
  display: flex;
  height: 40px;
  transition: all 0.4s ease;
}
.tabs.hidden {
  transform: translateX(-100%);
}

.tab {
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  cursor: pointer;
  transition: 0.15s ease-in-out;
}
.tab:hover {
  background: rgba(0, 0, 0, 0.15);
}
.tab.active {
  background: rgba(219, 10, 64, 0.15);
}
.tab.active svg path {
  fill: #db0a40;
}
.tab.active:after {
  opacity: 0;
}
.tab:after {
  content: "";
  background: rgba(0, 30, 60, 0.3);
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 3px;
  z-index: 999;
}
.tab svg {
  position: relative;
  top: 2px;
}
.tab svg path {
  fill: #ffe8ee;
}

.separator {
  width: 1px;
  background: rgba(0, 0, 0, 0.4);
  position: relative;
}
.separator:after {
  content: "";
  background: rgba(0, 30, 60, 0.3);
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 3px;
  z-index: 999;
}

sources.scss/base/_variables.scss:
// Variables go here
@import url("https://fonts.googleapis.com/css?family=Titillium+Web:400,600,700");

$blue: #001e3c;
$lightblue: #d2e8ff;

$black: darken($blue, 5%);

$red: #db0a40;
$lightred: #ffe8ee;
$radius: 4px;
$base-font: "Titillium Web", sans-serif;
$spacing: 12px;

.hidden {
  display: none;
}

