sources.scss/components/_home.scss:
.home {
    position: relative;
    margin-top: -1px;
    flex: 1;
    display: flex;
    flex-flow: column;


}
.home-header-container {
    position: absolute;
    left: 0;
    right: 0;
    top: 0;
    animation: fadeInOnly .5s ease-in-out forwards;
    animation-delay: .5s;
    opacity: 0;
    &:before {
        content: '';
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        background:#231C3C;
        z-index: 99;
        opacity: .6;
    }

    &:after {
        content: '';
        position: absolute;
        z-index: 999;
        top: 0;
        left: 0;
        bottom: 0;
        right: 0;
        background: linear-gradient(rgba(#231C3C, .4), #231C3C);
    }

    .home-header {
        filter: grayscale(100%);
        height: 70px;
        background-size: cover;
        background-position: center center;
    }

}

.home-content {
    position: relative;
    color: white;
    z-index: 999;
    padding: 0 $spacing;
    flex: 1;


    .main-title {
        min-height: 20px;
        font-size: 18px;
        text-align: center;
        margin: 0;
        margin-top: $spacing;
        opacity: 0;
        animation: fadeIn .3s ease-in-out forwards;
        animation-delay: .2s;
    }

    .subtitle {
        min-height: 38px;
        color: $lightblue;
        text-align: left;
        font-size: 14px;
        padding: 6px 0;
        padding-left: 10px;
        background: rgba($lightblue, .1);
        color: $lightblue;
        border-radius: 4px;
        margin: $spacing / 1.5 0;
        line-height: 1.4;
        font-weight: 600;
        position: relative;
        overflow: hidden;
        border: 1px solid $lightblue;
        border-left: none;
        animation: fadeIn .3s ease-in-out forwards;
        animation-delay: .3s;
        opacity: 0;
        &:before {
            content: '';
            position: absolute;
            left: 0;
            top: 0;
            bottom: 0;
            width: 3px;
            background: $lightblue;
        }

        .can-container {
            display: inline-block;
            position: relative;
            top: 3px;
            margin: 0 $spacing / 2;
        }
    }

    .text {
        font-size: 14px;
        line-height: 1.4;
        color:$lightblue;
                animation: fadeIn .3s ease-in-out forwards;
        animation-delay: .4s;
        opacity: 0;
    }
}

.home-cta {
    line-height: 39px;
    flex: 0 0 40px;
    font-size: 14px;
    font-weight: 600;
    text-align: center;
    background: rgba($red, 1);
