sources.scss/components/_leaderboard.scss:
.leaderboard {
    flex: 1;
    display: flex;
    flex-flow: column;
    padding: 0 $spacing;

    &-title {
      font-size: 18px;
      text-align: center;
      margin: 0;
      margin-top: $spacing * 1;
      color: white;
      font-weight: bold;
      opacity: 0;
      animation: fadeIn .3s ease-in-out forwards;
      animation-delay:0;
    }

    .list {
        width: 100%;
        flex: 1;
    }
}

.list {
    display: flex;
    flex-flow: column;
}
.list-item {
    display: none;
    border-bottom: 1px solid rgba($lightblue, .15);
    flex: 1;
    align-items: center;
    color: white;
    font-size: 14px;
    font-weight: 600;
    opacity: 0;

    &.active {
      display: flex;
    }

    &:last-child {
        border-bottom: none;
    }

    &-position {
        color: $lightblue;
        font-weight: 600;
        font-size: 14px;
    }

    &-avatar {
        height: 24px;
        width: 24px;
        background: rgba(white, .2);
        border-radius: 50%;
        margin: 0 $spacing / 1.5;
        background-size: cover;
    }

    &-score {
        font-size: 12px;
        color: $lightblue;
        display: flex;
        align-items: center;
        .can {
            margin-right: $spacing / 2;
        }
    }

    &-name {
        flex: 1;
        white-space: nowrap;
        overflow: hidden;
        text-overflow: ellipsis;
    }
}

.pagination {
    display: flex;
    height: 40px;
    border-top: 1px solid rgba($lightblue, .15);
    margin: 0 -($spacing) ;
    margin-top: 0;
    margin-bottom: 0;

    &.no-margin {
        margin: 0;
    }

    .arrow {
        flex: 1;
        display: flex;
        align-items: center;
        justify-content: center;
        transition: ease-in-out .15s all;
        cursor: pointer;

        &:hover {
            background: rgba(black, .15);
        }

        svg {
            position: relative;

            polyline {
                stroke: $lightblue;
            }
        }

        &-right {
            text-align: right;
            border-left: 1px solid rgba($lightblue, .15);

            svg {
                transform: rotate(180deg);

            }
        }
    }
}

.current-position {
    flex: 0 0 40px;
    background: rgba($lightblue, .2);
    margin-left: -($spacing);
    margin-right: -($spacing);
    padding: 0 $spacing;
    height: 40px;
    margin-bottom: 3px;
    animation: none !important;
    opacity: 1;
    .list-item-name {
        text-align: center;
    }

    & > * {
        position: relative;
        bottom: 0;
    }
}

.list-item {
    animation: fadeIn .3s ease-in-out forwards;
    &:nth-child(1) {
        animation-delay: .1s;
    }
    &:nth-child(2) {
        animation-delay: .2s;
    }

    &:nth-child(3) {
        animation-delay: .3s;
    }

    &:nth-child(4) {
        animation-delay: .4s;
    }
    //todo, manage animation for others pages
    // &:nth-child(5) {
    //     animation-delay: .5s;
    // }
}

@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(6px);
    }

    to {
        transform: translateY(0);
        opacity: 1;
    }
}

@keyframes fadeInOnly {
    from {
        opacity: 0;
    }

    to {
        transform: translateY(0);
        opacity: 1;
    }
}

