sources.scss/components/_poll.scss:
$small: 600px;
$lg: 1200px;
$md: 900px;
$m-800:  800px;
$m-700: 700px;

@mixin respond-to($media) {
  @if $media == handhelds {
    @media only screen and (max-width: $small) { @content; }
  }
  @if $media == larged {
    @media only screen and (max-width: $lg) { @content; }
  }
  @if $media == mediumd {
    @media only screen and (max-width: $md) { @content; }
  }
  @if $media == m-800 {
    @media only screen and (max-width: $m-800) { @content; }
  }
  @if $media == m-700 {
    @media only screen and (max-width: $m-700) { @content; }
  }
}
@mixin respond-to-sup($media) {
  @media only screen and (min-width: $media) { @content; }
}


.poll-container {
  position: absolute;
  top: 0; left: 0; bottom: 0; right: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  pointer-events: none;
}

.poll.opened{
  pointer-events: auto;
}

.poll {

  &-title {
    color: white;
    font-weight: 700;
    text-align: center;
    padding: 0 $spacing;
    height: 80px;
    display: flex;
    align-items: center;
    font-size: 20px;
    line-height: 1.2;
    justify-content: center;
  }
}

.poll-header {
  padding: $spacing;
  padding-bottom: 0;
  font-size: 14px;
  font-weight: 600;
  color: $red;
  display: flex;
  justify-content: center;

  &-title {
    font-weight: 700;
    flex: 1;
  }
}

.poll-body {
  min-height: 170px;
  &.boximg{
    min-height: 235px;
  }
}


.poll-counter-container {
  text-align: center;
  position: relative;
}

.poll-counter-value {
  position: absolute;
  left: 1px;
  right: 0;
  top: 0;
  bottom: 1px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: $lightblue;
  font-weight: 800;
}

.progress {
  transform: rotate(-90deg);
  width: 30px;
  height: 30px;
  display: block;
}

.progress__value {
  animation: progress 60s linear infinite;
  stroke-dasharray: 339.292;
  stroke-dashoffset: 339.292;
  stroke: rgba($lightblue, 0.3);
}

.poll-cross {
  flex: 1;
  display: flex;
  justify-content: flex-end;
  opacity: 0.5;

  svg {
    position: relative;
    top: 2px;
  }

  svg path {
    stroke: $lightred !important;
  }
}

.poll-answers {
  display: flex;
  padding: 0 $spacing;
  flex: 1;
  padding-bottom: $spacing * 1.5;
  justify-content: center;
  // @media(max-width: 410px) {
  //   display: block;

  //   .poll-answer {
  //     display: flex;
  //     flex-flow: row !important;
  //     padding: 2px 0;
  //     width: 100%;
  //     max-width: 100% !important;
  //     width: auto !important;
  //     &-image {
  //       flex: 0 0 32px;
  //       height: 32px !important;
  //       margin-right: 4px;
  //       border-radius: 3px !important;
  //     }

  //     &-label {
  //       flex: 1;
  //       justify-content: flex-start !important;
  //       padding-left: 8px;
  //       border-radius: 3px !important;
  //     }

  //     &-button {
  //       margin-left: 4px;
  //       border-radius: 3px !important;
  //       display: flex;
  //       align-items: center;
  //       justify-content: center;
  //       flex: 0 0 80px;
  //     }

  //     &-percent {
  //       height: 40Px;
  //       flex: 0 0 80px;
  //       margin-left: 4px;
  //       border-radius: 3px !important;
  //     }
  //   }
  // }

  &.list {
    display: block;
  }

  .poll-answer {
    text-align: center;
    margin: 0 $spacing / 4;
    display: flex;
    flex-flow: column;
    cursor: pointer;
    width: 70px;
    max-width: 100px;

    &.selected {

      &:hover {

      }
      .poll-answer-label {
        background: white;
        color: $red !important;
        text-shadow: none;
        font-weight: 900;
      }
    }

    &:hover {
      .poll-answer-image:before {
        opacity: 0;
      }

      .poll-answer-label {
        color: white;
      }
    }

    &-image {
      height: 70px;
      background-size: cover;
      border-radius: $radius $radius 0 0;
      background-position: center center;
      position: relative;
      cursor: pointer;

      .check {
        position: absolute;
        right: 0;
        bottom: 0;
        margin: 4px;
        width: 16px;
        height: 16px;
      }

      &:before {
        content: "";
        opacity: 0;
        background: rgba($black, 0.7);
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        transition: 0.5s ease-in-out;
        border-radius: $radius $radius 0 0;
      }

      &:after {
        content: "";
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        border-left: 1px solid rgba($lightblue, 0.1);
        border-top: 1px solid rgba($lightblue, 0.1);
        border-right: 1px solid rgba($lightblue, 0.1);
        border-radius: $radius $radius 0 0;
      }
    }

    &-label {
      line-height: 1;

      min-height: 32px;
      white-space: nowrap;
       overflow: hidden;
       text-overflow: ellipsis;
      padding-bottom: 1px;
      display: flex;
      align-items: center;
      justify-content: center;
      color: $lightblue;
      font-size: 14px;
      font-weight: 600;
      bottom: 0;
      left: 0;
      width: 100%;
      background: rgba($lightblue, .2);
      text-shadow: 0px 1px 1px rgba(black, 0.5);
      right: 0;
    }

    &-button {
      color: white;
      font-size: 13px;
      text-shadow: 0px -1px 0px rgba(black, 0.4);
      background: $red;
      font-weight: 900;
      padding: ($spacing / 2) 0;
      border-radius: 0 0 $radius $radius;

      &:hover {
        background: darken($red, 5%);
      }
    }
  }
}

.poll-answers {
  &:hover {
    .poll-answer-image {
      &:before {
        content: "";
        opacity: 1;
      }
    }
  }
}

.poll-answer-percent {
  height: 32px;
  background: rgba($red, 0.3);
  border-radius: 0 0 $radius $radius;
  color: white;
  display: flex;
  align-items: center;
  position: relative;
  justify-content: center;
  overflow: hidden;

  span {
    position: relative;
    font-weight: 900;
    font-size: 14px;
  }

  &-value {
    position: absolute;
    left: 0;
    top: 0;
    bottom: 0;
    background: $red;
  }
}


.poll-answer-list-item {
  box-shadow: 0px 0px 0px 1px $red inset;
  margin-bottom: $spacing / 2;
  border-radius: $radius;
  color: $red;
  text-align: center;
  line-height: 30px;
  font-weight: 600;
  position: relative;
  cursor: pointer;
  transition: 0.15s ease-in-out;
  font-size: 14px;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;

  .check {
    position: relative;
    z-index: 9;
    top: 1px;
    width: 20px;
    height: 20px;
  }

  &.result {
    color: white;
    text-align: left;
    padding-left: $spacing / 2;
    justify-content: flex-start;
    border-color: transparent;
    box-shadow: none;

    &:hover {
      background: none;

      .poll-answer-list-item-value:not(.selected) {
        background: rgba($lightred, 0.3);
      }
    }
  }

  &:hover {
    background: rgba($red, 0.15);
  }

  &-name {
    position: relative;
    z-index: 99;
  }

  &-value {
    height: 100%;
    background: rgba($lightred, 0.2);
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    transition: background 0.3s ease-in-out;

    &.selected {
      background: $red;
    }
  }

  &-percent {
    position: absolute;
    right: 0;
    top: 0;
    margin-right: $spacing / 2;
    color: $lightred;
  }
}

@keyframes progress {
  from {
    stroke-dashoffset: 0;
  }
  to {
    stroke-dashoffset: -339.292;
  }
}

.popupVotePositionRight .box.poll {
  width:35vw;
  max-width: 35vw;
  height: 50vh;
  min-width: unset;
  left: auto;
  right: 2.5%;
  transform: translate(0, -50%);

  .duration {
    left: 0.1vw;
    right: 0;
    top: 0;
    bottom: 0.1vw;
  }
  .progress {
    width: 3.0vw;
    height: 3.0vw;
  }
  .poll-cross svg {
    top: 0.2vw;
    width: 1.4vw;
    height: 1.4vw;
  }
  .poll-header {
    font-size: 1.4vw;
    padding: 1.2vw;
    padding-bottom: 0;
  }
  .poll-title{
    font-size: 1.98vw;
    padding: 0 1.2vw;
    height: 8.0vw;
  }

  .poll-answers {
    justify-content: center;
    padding: 0 1.2vw;
  }

  .poll-answer-list-item {
    margin-bottom: 0.6vw;
    border-radius: 0.4vw;
    line-height: 3.0vw;
    font-size: 1.4vw;
  }

  .poll-answer {
    width: 7vw;
    max-width: unset;
    margin: 0 0.3vw;

    .label {
      font-size: 1.4vw;
    }

    .poll-answer-label {
      min-height: 3.2vw;
      padding-bottom: 0.1vw;
      font-size: 1.4vw;
    }

    .poll-answer-button {
      font-size: 1.3vw;
      padding: 0.6vw 0;
      border-radius: 0 0 0.4vw 0.4vw;
    }

    .poll-answer-image {
      height: 7vw;
      border-radius: 0.4vw 0.4vw 0 0;
      .check {
        margin: 0.4vw;
        width: 1.6vw;
        height: 1.6vw;
      }
    }

    .poll-answer-percent {
      height: 3.2vw;
      font-size: 1.4vw;
      border-radius: 0 0 0.4vw 0.4vw;
      span {
        font-size: 1.4vw;
      }
    }
  }
}

sources.scss/components/_profile.scss:
.slider {
  flex: 1;
  display: flex;
  transition: all 0.4s ease;

  &.detail {
    transform: translateX(-100%);
  }
}
.profile-header {
  color: white;
  position: relative;
  flex: 0 0 60px;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-top: -1px;
  &:after {
    opacity: 0;
    content: "";
    background: $red;
    position: absolute;
    animation: header 0.3s ease-in-out forwards;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    margin: auto;
  }

  .profile-header-content {
    opacity: 0;
    animation: fadeIn 0.3s ease-in-out forwards;
    animation-delay: 0.15s;
    position: relative;
    z-index: 3;
  }

  canvas {
    position: absolute;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
  }
}

.profile-name {
  text-align: center;
  font-size: 18px;
  font-weight: 900;
}

.profile-tagline {
  display: flex;
  justify-content: center;
  font-size: 14px;
  font-weight: bold;
  color: $lightred;

  & > * {
    margin: 0 $spacing / 3;
  }
}

.profile-can-count {
  display: flex;
  animation: fadeIn 0.3s ease-in-out forwards;
  opacity: 0;
  animation-delay: 0.2s;

  .can-container {
    position: relative;
    top: 3px;
    margin-right: 6px;
  }
}

.profile-rank {
  opacity: 0;
  animation: fadeIn 0.3s ease-in-out forwards;
  animation-delay: 0.25s;
  span {
    opacity: 0.5;
    font-weight: 600;
    margin-right: 2px;
  }
}

.poll-index {
  padding: 0 $spacing;
  flex: 1;
  display: flex;
  flex-flow: column;
}

.profile {
  flex: 0 0 100%;
  display: flex;
  flex-flow: column;
}

.poll-item {
  display: none;
  align-items: center;
  font-size: 14px;
  border-bottom: 1px solid rgba($lightblue, 0.15);
  animation: fadeIn 0.3s ease-in-out forwards;
  opacity: 0;
  cursor: pointer;
  flex: 0 0 53px;

  &-arrow {
    flex: 0 0 10px;
    position: absolute;
    right: 0;
    img {
      display: block;
    }
  }

  &.active {
    display: flex;
  }

  &-inner {
    display: flex;
    align-items: center;
    padding: ($spacing / 2) ($spacing);
    margin-left: -($spacing);
    margin-right: -($spacing);
    border-radius: 4px;
    width: 100%;

    &:hover {
      background: rgba(black, 0.15);
    }
  }

  &.active:nth-child(1) {
    animation-delay: 0.3s;
  }
  &.active:nth-child(2) {
    animation-delay: 0.4s;
  }

  &.active:nth-child(3) {
    animation-delay: 0.5s;
  }

  &.active:last-child {
    border-bottom: none;
  }

  &.won {
    .poll-item-answer {
      color: $red;
    }
  }

  &.no_answer {
    .poll-item-answer {
      color: rgba($lightblue, 0.5) !important;
    }
  }

  &-icon {
    margin-right: $spacing;
  }

  &-content {
    flex: 1;
  }

  &-title {
    max-width: 165px;
    color: white;
    font-weight: 600;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    width: 99%;
    display: inline-block;
    max-width: 180px;
  }

  &-answer {
    color: $lightblue;
    margin-top: -4px;
    font-weight: 600;
  }
}

@keyframes header {
  from {
    height: 0;
    opacity: 0;
  }

  to {
    opacity: 1;
    height: 60px;
  }
}

sources.scss/components/_tabss.scss:
.tabs {
  display: flex;
  height: 40px;
  transition: all 0.4s ease;

  &.hidden {
    transform: translateX(-100%);
  }
}

.tab {
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  cursor: pointer;
  transition: 0.15s ease-in-out;

  &:hover {
    background: rgba(black, 0.15);
  }

  &.active {
    background: rgba($red, 0.15);

    svg {
      path {
        fill: $red;
      }
    }

    &:after {
      opacity: 0;
    }
  }

  &:after {
    content: "";
    background: rgba($blue, 0.3);
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 3px;
    z-index: 999;
  }

  svg {
    position: relative;
    top: 2px;
    path {
      fill: $lightred;
    }
  }
}

.separator {
  width: 1px;
  background: rgba(black, 0.4);
  position: relative;
  &:after {
    content: "";
    background: rgba($blue, 0.3);
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 3px;
    z-index: 999;
  }
}
